document.addEventListener("DOMContentLoaded",(function(){headerFunctions(),mobMenu()}));const headerFunctions=()=>{window.addEventListener("scroll",(function(){window.pageYOffset>=130?document.querySelector(".header").classList.add("scrolled"):document.querySelector(".header").classList.remove("scrolled")}))},mobMenu=()=>{const e=document.querySelector(".header");document.querySelector(".bar");document.querySelector(".bar").addEventListener("click",(function(){e.classList.contains("active-mobile")?e.classList.remove("active-mobile"):e.classList.add("active-mobile")}))};