document.addEventListener("DOMContentLoaded",(function(){headerFunctions(),mobMenu()}));const headerFunctions=()=>{if(document.querySelector("#intro")){Number.parseInt(window.getComputedStyle(document.querySelector("#intro")).paddingTop.replace("px",""))}window.addEventListener("scroll",(function(){window.pageYOffset>=110?document.querySelector(".header").classList.add("scrolled"):document.querySelector(".header").classList.remove("scrolled")}))},mobMenu=()=>{const e=document.querySelector(".header"),t=document.querySelector(".bar"),n=document.querySelectorAll(".nav .nav__item");function o(n){n?(e.classList.remove("active-mobile"),t.classList.remove("active-mobile")):(e.classList.add("active-mobile"),t.classList.add("active-mobile"))}document.querySelector(".bar").addEventListener("click",(function(){o(e.classList.contains("active-mobile"))})),window.innerWidth<769&&n.forEach(e=>{e.addEventListener("click",(function(){o(!0)}))})};